    def Delete_question(questionid, tablename):
        sql = sqlite3.connect("quiz.db")
        cur = sql.cursor()
        querry = "DELETE FROM "+tablename+" WHERE questionid="+questionid
        cur.execute(querry)
        sql.commit()
        sql.close()
    def SelectAll_Tables():
        sql = sqlite3.connect("quiz.db")
        cur = sql.cursor()
